import React from "react";
import { Button, InputGroup } from "@blueprintjs/core"
import { isNull } from "util"
interface IMainMenuProps { // Args
}

interface IMainMenuState {
    lobbyKey: string
    playerName: string
}

export class MainMenu extends React.PureComponent<IMainMenuProps, IMainMenuState>{

    constructor(props: any) {
        super(props);
        this.state = {
            lobbyKey: "",
            playerName: "",
        };
    }

    private updateLobbyKey = (event: any) => {
        let val = event.target.value;
        this.setState({
            lobbyKey: val,
        })
    }

    private updatePlayerName = (event: any) => {
        let val = event.target.value;
        this.setState({
            playerName: val,
        })
    }

    private sendLobbyRequest = () => {
        // From Prop?
        console.log(this.state.lobbyKey)
        console.log(this.state.playerName)

    }

    public render() {
        return (
          <div>
            {<InputGroup large={true} onChange={this.updateLobbyKey}/>}
            {<InputGroup large={true} onChange={this.updatePlayerName}/>}
            {<Button text="Request" onClick={this.sendLobbyRequest}/>} 
          </div>
        );
    }

}

export default MainMenu;
