import React from "react";
import { LobbySelection } from "./LobbySelection/LobbySelection"
import socketIOClient from "socket.io-client";
const ENDPOINT = "http://127.0.0.1:5000";

interface IMainProps { // Args
}

interface IMainState {
    menu: string,
}

export class Main extends React.PureComponent<IMainProps, IMainState>{

    constructor(props: any) {
        super(props);
        this.state = {
            menu: "LobbySelection",
        };

        const socket = socketIOClient(ENDPOINT);
        socket.on("LobbyChange", (data: any) => {
          this.setState({
            menu: data
          })
        });
    }

    public render() {
        return (
          <div style={{}}>
            {/* LATEST ERROR MESSAGE */}
            {this.state.menu === "LobbySelection" && <LobbySelection/>} 
          </div>
        );
    }

}

export default Main;
