import React from "react";
import { LobbySelection } from "./Lobby/LobbySelection"
import socketIOClient from "socket.io-client";
import Lobby from "./Lobby/Lobby";
import { Toast, Intent } from "@blueprintjs/core";
const ENDPOINT = "http://127.0.0.1:5000";

interface IMainProps { // Args
}

interface IMainState {
    menu: string,
    socket: any,
    gameState: any
    toastMessage: string
    showToast: boolean
}

export class Main extends React.PureComponent<IMainProps, IMainState>{

    constructor(props: any) {
        super(props);
        this.state = {
            menu: "LobbySelection",
            socket: socketIOClient(ENDPOINT),
            gameState: {},
            toastMessage: "",
            showToast: false,
        };
        this.state.socket.on("LobbyRequestResponse", (data: any) => {
            console.log("LobbyChange")
            console.log(data)
            let msg = data["message"]
            if (data['approved']) {
                console.log('approved')
                this.setState({
                    menu: "Lobby",
                })
            } else {
                console.log(msg)
                this.setState({
                    toastMessage: msg,
                    showToast: true
                })
            }
        });
        this.state.socket.on("GameUpdate", (data: any) => {
            console.log("GameUpdate")
            console.log(data)
            this.setState({
                gameState: data
            })
        });
    }

    public render() {
        return (
          <div style={{}}>

            {this.state.showToast && <Toast message={this.state.toastMessage} intent={Intent.WARNING} />}
            {this.state.menu === "LobbySelection" && <LobbySelection socket={this.state.socket}/>} 
            {this.state.menu === "Lobby" && <Lobby socket={this.state.socket} gameState={this.state.gameState}/>} 
          </div>
        );
    }

}

export default Main;
