}

interface IMainState {
    menu: string,
}

export class Main extends React.PureComponent<IMainProps, IMainState>{

    constructor(props: any) {
        super(props);
        this.state = {
            menu: "Menu",
        };

        const socket = socketIOClient(ENDPOINT);
        // socket.on("FromAPI", (data: any) => {
        //   this.setState({
