        // });
    }

    public render() {
        return (
          <div style={{backgroundImage: `url(${background})`}}>
            {/* LATEST ERROR MESSAGE */}
            {this.state.menu === "Menu" && <MainMenu/>} 
          </div>
        );
    }

}

export default Main;
