import React from "react";
import { Button, InputGroup, Card } from "@blueprintjs/core"
interface ILobbyProps { // Args
    socket: any
    gameState: any
}

interface ILobbyState {
}

export class Lobby extends React.PureComponent<ILobbyProps, ILobbyState>{

    constructor(props: any) {
        super(props);
        this.state = {};
    }


    private sendStartGameRequest = () => {
        this.props.socket.emit("StartGame",{})
    }

    private RenderPlayers = () => {
        let players: any = []
        if (this.props.gameState === {}) {
            return <div></div>
        }
        if (!this.props.gameState.hasOwnProperty("players")) {
            return <div></div>
        }
        for (let player in this.props.gameState.players) {
            let p = player as any
            players.add(
                <Card> 
                    {player.playerName}
                </Card>
            )
        }

        return (
            {players}
        )
    }

    public render() {
        return (
          <div style={{}}>
                <div>
                    {this.RenderPlayers()}
                </div>
            {<Button style={{}} text="Start Game" onClick={this.sendStartGameRequest}/>} 
          </div>
        );
    }

}
export default Lobby;
