import React from "react";
import { LobbySelection } from "./LobbySelection/LobbySelection"
import socketIOClient from "socket.io-client";
const ENDPOINT = "http://127.0.0.1:5000";

interface IMainProps { // Args
}

interface IMainState {
    menu: string,
    socket: any,
}

export class Main extends React.PureComponent<IMainProps, IMainState>{

    constructor(props: any) {
        super(props);
        this.state = {
            menu: "LobbySelection",
            // socket: socketIOClient(ENDPOINT),
            // @ts-ignore
            socket: io.connect()
        };

        // const socket = ;
        // this.setState({
        //     socket: socket
        // })
        this.state.socket.on("LobbyChange", (data: any) => {
            if (data['approved'] == true) {
                this.setState({
                    menu: data['menu']
                })
            }
        });
    }

    public render() {
        return (
          <div style={{}}>
            {/* LATEST ERROR MESSAGE */}
            {this.state.menu === "LobbySelection" && <LobbySelection socket={this.state.socket}/>} 
          </div>
        );
    }

}

export default Main;
